import React, { useState, useEffect } from 'react';
import Checklist from './components/Checklist';
import Ingredient from './components/Ingredient';

function CocktailsApp() {
  const [cocktails, setCocktails] = useState([]);
  const [selectedIngredients, setSelectedIngredients] = useState([]);
  const [filteredCocktails, setFilteredCocktails] = useState([]);

  useEffect(() => {
    // Charger les cocktails à partir du fichier JSON
    const data = require('./Cocktails.json');
    setCocktails(data);
  }, []);

  useEffect(() => {
    // Filtrer les cocktails en fonction des ingrédients sélectionnés
    if (selectedIngredients.length === 0) {
      setFilteredCocktails(cocktails); // Si aucun ingrédient n'est sélectionné, afficher tous les cocktails
    } else {
      const filtered = cocktails.filter(cocktail =>
        selectedIngredients.every(selectedIngredient =>
          cocktail.ingredients.some(ingredient =>
            ingredient.alcool && ingredient.alcool.toLowerCase() === selectedIngredient.toLowerCase()
          )
        )
      );
      setFilteredCocktails(filtered);
    }
  }, [selectedIngredients, cocktails]);

  const handleIngredientToggle = ingredient => {
    if (selectedIngredients.includes(ingredient)) {
      setSelectedIngredients(selectedIngredients.filter(selected => selected !== ingredient));
    } else {
      setSelectedIngredients([...selectedIngredients, ingredient]);
    }
  };

  return (
    <div>
      <h1>Liste des Cocktails</h1>
      <div>
        <h2>Filtrer par ingrédient :</h2>
        <Checklist items={cocktails.flatMap(cocktail => cocktail.ingredients.map(ingredient => ingredient.alcool.toLowerCase())).filter((value, index, self) => self.indexOf(value) === index)} selectedItems={selectedIngredients} onToggle={handleIngredientToggle} />
      </div>
      {/* Afficher les cocktails filtrés */}
      {filteredCocktails.map(cocktail => (
        <div key={cocktail.nom}>
          <h2>{cocktail.nom}</h2>
          <p>{cocktail.description}</p>
          <h3>Ingrédients :</h3>
          <ul>
            {cocktail.ingredients.map((ingredient, index) => (
              <Ingredient key={index} name={ingredient.nom} dose={ingredient.dose} alcohol={ingredient.alcool} />
            ))}
          </ul>
        </div>
      ))}
    </div>
  );
}

export default CocktailsApp;
