import React, { useState, useEffect } from 'react';

function CocktailsApp() {
  const [cocktails, setCocktails] = useState([]);
  const [selectedAlcohol, setSelectedAlcohol] = useState('');
  const [filteredCocktails, setFilteredCocktails] = useState([]);

  useEffect(() => {
    // Charger les cocktails à partir du fichier JSON
    const data = require('./Cocktails.json');
    setCocktails(data);
    setFilteredCocktails(data); // Initialiser les cocktails filtrés avec tous les cocktails
  }, []);

  useEffect(() => {
    // Filtrer les cocktails en fonction de l'alcool sélectionné
    if (selectedAlcohol === '') {
      setFilteredCocktails(cocktails); // Si aucun alcool n'est sélectionné, afficher tous les cocktails
    } else {
      const filtered = cocktails.filter(cocktail =>
        cocktail.ingredients.some(ingredient => ingredient.nom.toLowerCase().includes(selectedAlcohol.toLowerCase()))
      );
      setFilteredCocktails(filtered);
    }
  }, [selectedAlcohol, cocktails]);

  const handleAlcoholChange = event => {
    setSelectedAlcohol(event.target.value);
  };

  return (
    <div>
      <h1>Liste des Cocktails</h1>
      <div>
        <label htmlFor="alcoholSelect">Choisir un alcool :</label>
        <select id="alcoholSelect" value={selectedAlcohol} onChange={handleAlcoholChange}>
          <option value="">Tous les alcools</option>
          {/* Générer les options de sélection en fonction des alcools disponibles */}
          {cocktails.flatMap(cocktail => cocktail.ingredients.map(ingredient => ingredient.nom.toLowerCase())).filter((value, index, self) => self.indexOf(value) === index).map((alcohol, index) => (
            <option key={index} value={alcohol}>{alcohol}</option>
          ))}
        </select>
      </div>
      {/* Afficher les cocktails filtrés */}
      {filteredCocktails.map(cocktail => (
        <div key={cocktail.nom}>
          <h2>{cocktail.nom}</h2>
          <p>{cocktail.description}</p>
          <h3>Ingrédients :</h3>
          <ul>
            {cocktail.ingredients.map((ingredient, index) => (
              <li key={index}>{`${ingredient.nom} - ${ingredient.dose}`}</li>
            ))}
          </ul>
        </div>
      ))}
    </div>
  );
}

export default CocktailsApp;
