import React, { useState, useEffect } from 'react';

function CocktailsApp() {
  const [cocktails, setCocktails] = useState([]);

  useEffect(() => {
    // Simuler le chargement des données depuis le fichier JSON
    // Dans un vrai projet, vous utiliserez fetch() ou Axios pour cela
    // Par exemple : fetch('/chemin/vers/votre/fichier.json')
    const data = require('./Cocktails.json');
    setCocktails(data);
  }, []);

  return (
    <div>
      <h1>Liste des Cocktails</h1>
      {cocktails.map(cocktail => (
        <div key={cocktail.nom}>
          <h2>{cocktail.nom}</h2>
          <p>{cocktail.description}</p>
          <h3>Ingrédients :</h3>
          <ul>
            {cocktail.ingredients.map(ingredient => (
              <li key={ingredient.nom}>{`${ingredient.nom} - ${ingredient.dose}`}</li>
            ))}
          </ul>
        </div>
      ))}
    </div>
  );
}

export default CocktailsApp;
